sources.sass/_header.scss:
/*Section header*/

.section-header {
  background-color: $main-hero-bg-color;
  width: 100%;

  @media screen and (max-width: 767px) {
    background-image: url('../images/ellipse-mobile.png');
    background-repeat: no-repeat;
    background-position: right;
  }

  @media screen and (min-width: 768px) {
    background-image: url('../images/ellipse-tablet.png');
    background-repeat: no-repeat;
    background-position: 65%;
  }

  @media screen and (min-width: 1280px) {
    background-image: url('../images/ellipse.png');
    background-repeat: no-repeat;
    background-position: 65%;
  }
}

.container.header {
  padding-top: 37px;
  @media screen and (max-width: 479px) {
    background-image: url('../images/icecream-mobile.png');
    background-repeat: no-repeat;
    background-position-x: 80%;
    background-position-y: 100%;
    padding-bottom: 160px;
    padding-top: 6px;
  }
  @media screen and (min-width: 480px) {
    background-image: url('../images/icecream-mobile.png');
    background-repeat: no-repeat;
    background-position-x: 80%;
    background-position-y: 100%;
    padding-bottom: 160px;
    padding-top: 6px;
  }

  @media screen and (min-width: 768px) {
    background-image: url('../images/icecream-tablet.png');
    background-repeat: no-repeat;
    background-position-y: bottom;
    background-position-x: 63%;
    padding: 37px 34px 59px 34px;
  }

  @media screen and (min-width: 1280px) {
    background-image: none;
    padding-bottom: 81px;
  }
}

.menu {
  @media screen and (max-width: 767px) {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 137px;
  }

  @media screen and (min-width: 768px) {
    margin-left: 0;
    margin-bottom: 15px;
    display: flex;
    justify-content: space-between;
  }

  @media screen and (min-width: 1280px) {
    display: flex;
    justify-content: space-around;
    align-items: center;
    margin-bottom: 84px;
  }

  &__icon {
    fill: $text-title-color;
  }
}

.menu-nav {
  list-style: none;
  display: flex;
  margin: 0;
  padding: 0;
  @media screen and (max-width: 1279px) {
    display: none;
  }

  &__link {
    text-decoration: none;
    font-family: $main-font;
    font-style: normal;
    font-weight: 700;
    font-size: 16px;
    line-height: 1.31;

    align-items: center;

    color: $main-bg-color;
    &:hover,
    &:focus {
      text-decoration: underline;
      color: $text-title-color;
    }
  }

  &__link--current {
    color: $text-title-color;
  }

  &__item:not(:last-child) {
    margin-right: 35px;
  }

  &__modal {
    font-family: $main-font;
    font-style: normal;
    font-weight: 700;
    font-size: 16px;
    line-height: 1.31;

    align-items: center;
    text-align: center;

    padding: 14px 53px 14px 37px;

    color: $text-title-color;
    background: $main-bg-color;
    border-radius: 22px;
    border: none;
    cursor: pointer;
    transition: background-color 1s linear;
    -webkit-animation: pulsing 2s infinite;
    animation: pulsing 2s infinite;

    @-webkit-keyframes pulsing {
      0% {
        -webkit-transform: scale(0.5, 0.5);
        transform: scale(0.5, 0.5);
      }
      50% {
        -webkit-transform: scale(1, 1);
        transform: scale(1, 1);
      }
      100% {
        -webkit-transform: scale(0.5, 0.5);
        transform: scale(0.5, 0.5);
      }
    }

    @keyframes pulsing {
      0% {
        -webkit-transform: scale(0.5, 0.5);
        transform: scale(0.5, 0.5);
      }
      50% {
        -webkit-transform: scale(1, 1);
        transform: scale(1, 1);
      }
      100% {
        -webkit-transform: scale(0.5, 0.5);
        transform: scale(0.5, 0.5);
      }
    }

    @media screen and (max-width: 767px) {
      display: none;
    }
    @media screen and (max-width: 1279px) {
      height: 50px;
    }

    &:hover,
    &:focus {
      background-color: #ecebeb;
    }

    &::after {
      display: inline-block;
      margin-left: 8px;
      content: '';
      width: 10px;
      height: 10px;
      background-image: url(../images/icon-contacts-bt2.svg);
      background-repeat: no-repeat;
      background-position: center;
      background-size: contain;
      @media screen and (min-width: 768px) {
        margin-left: 13.4px;
      }
      @media screen and (min-width: 1280px) {
        margin-left: 13.62px;
      }
    }
  }
}

.icecream {
  @media screen and (min-width: 768px) {
    margin-bottom: 66px;
  }
  @media screen and (min-width: 1280px) {
    padding-left: 0;
    margin-bottom: 112px;
    padding-left: 81px;
  }

  &__title {
    font-family: $second-font;
    font-style: normal;
    font-weight: 400;
    text-transform: uppercase;

    color: $main-bg-color;

    @media screen and (max-width: 767px) {
      margin-bottom: 19px;
      font-size: 26px; /*8.13vw*/
      line-height: 1.15;
    }
    @media screen and (min-width: 768px) {
      margin-bottom: 20px;
      font-size: 22px;
      line-height: 1.14;
    }
    @media screen and (min-width: 1280px) {
      margin-bottom: 20px;
      font-size: 38px;
      line-height: 1.16;
    }
  }
  &__title-passion {
    font-family: $second-font;
    font-style: normal;
    font-weight: 400;
    text-transform: uppercase;

    color: $text-title-color;

    @media screen and (max-width: 767px) {
      margin-bottom: 19px;
      font-size: 48px; /*6.23; /*48px*/
      line-height: 0.96;
    }
    @media screen and (min-width: 768px) {
      margin-bottom: 20px;
      font-size: 34px;
      line-height: 0.91;
    }
    @media screen and (min-width: 1280px) {
      margin-bottom: 20px;
      font-size: 58px;
      line-height: 0.89;
    }
  }
}

