    fill: #ffffff;
    // background: $main-hero-bg-color;
  }
}
/* свойства для блока, содержащего основное содержимое окна */
.modal-header-body {
  font-family: $second-font;
  position: relative;
  -webkit-box-flex: 1;
  -webkit-flex: 1 1 auto;
  -ms-flex: 1 1 auto;
  flex: 1 1 auto;
  padding: 15px;
  overflow: auto;
  color: #cea0bf;
  text-shadow: 1px 1px 2px black, 0 0 1em rgb(248, 245, 245);

  @media (max-width: 467px) {
    font-size: 10px;
  }
}

