sources.sass/_products.scss:
.products {
  padding-top: 121px;
  padding-bottom: 98px;

  @media screen and (min-width: 768px) {
    padding-top: 122px;
    padding-bottom: 112px;
  }

  @media screen and (min-width: 1280px) {
    padding-top: 127px;
    padding-bottom: 119px;
  }
}

.products__label {
  font-family: $second-font;
  padding: 0;
  margin: 0;
  margin-bottom: 15px;
  transform: scale(0) translateX(90%);
  transition: all 0.8s ease 0s;
  opacity: 0;

  &._active {
    transform: scale(1) translateX(0);
    opacity: 1;
  }

  @media screen and (min-width: 1280px) {
    font-size: 30px;
    line-height: 1.13;
  }
}

.products__title {
  font-family: $second-font;
  padding: 0;
  margin: 0;
  margin-bottom: 144px;

  transform: scale(0) translateX(-90%);
  transition: all 0.8s ease 0s;
  opacity: 0;

  &._active {
    transform: scale(1) translateX(0);
    opacity: 1;
  }

  @media screen and (min-width: 768px) {
    line-height: 1.125;
    margin-bottom: 150px;
  }

  @media screen and (min-width: 1280px) {
    margin-bottom: 259px;
  }
}

.products__list {
  list-style: none;
  margin: 0;
  padding: 0;

  @media screen and (min-width: 768px) {
    display: flex;
    justify-content: center;
    margin: -10px;
  }

  @media screen and (min-width: 1260px) {
    display: flex;
    justify-content: center;
    margin: -15px;
  }
}

.products__item {
  border-radius: $product-card-border;
  text-align: center;
  height: 485px;
  transform: scale(0) translateX(-360%);
  opacity: 0;
  transition: all 0.8s ease 0s;

  &:not(:last-child) {
    @media screen and (max-width: 767px) {
      margin-bottom: 130px;
    }
  }

  &._active {
    transform: scale(1) translateX(0);
    transition: all 0.8s ease 0s;
    opacity: 1;

    &:nth-child(2) {
      transition: all 0.8s ease 0.3s;
    }

    &:nth-child(3) {
      transition: all 0.8s ease 0.6s;
    }

    @media screen and (max-width: 767px) {
      width: 100%;
      max-width: 480px;
      text-align: center;
      align-items: center;
      margin-left: auto;
      margin-right: auto;
    }

    @media screen and (min-width: 768px) {
      flex-basis: calc((100% - 3 * 20px) / 3);
      margin: 10px;
    }

    @media screen and (min-width: 1260px) {
      flex-basis: calc((100% - 3 * 30px) / 3);
      margin: 15px;
      height: 525px;
    }
  }
}

