.products__info {
  font-family: $main-font;
  font-style: normal;
  font-weight: bold;
  font-size: 14px;
  line-height: 1.5;
  text-align: center;
  letter-spacing: 0.04em;
  color: $main-bg-color;
  margin: 30px 39px 0 41px;
  height: 124px;
  overflow: hidden;


  @media screen and (min-width: 768px) {
    margin: 30px 11px 40px 11px;
    height: 124px;
    overflow: hidden;
    line-height: 1.78;
  }

  @media screen and (min-width: 1280px) {
    font-size: 16px;
    margin: 32px 44px 62px 44px;
    height: 106px;
    overflow: hidden;
    line-height: 1.75;
  }
}

.products__info-modal {
  height: 100%;
  font-size: 14px;
  margin: 0;


}

.button__products {
  background-color: $main-hero-bg-color;
  border: none;
  cursor: pointer;

  @media screen and (max-width: 767px) {
    position: absolute;
    bottom: 30px;
    left: 40%;
  }
}

.button__products-icecoffee {
  background-color: $bg-color-product-second;
  border: none;
}

.button__products-milkshakes {
  background-color: $bg-color-product-third;
  border: none;
}

.backdrop {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.2);
  opacity: 1;
  z-index: 99;
  transition: opacity 250ms cubic-bezier(0.4, 0, 0.2, 1);
  overflow-y: scroll;
}

.backdrop--hidden {
  opacity: 0;
  pointer-events: none;
  visibility: initial;
}

.modal__icecream {
  background-color: $main-hero-bg-color;
}

.modal__icecoffee {
  background-color: $bg-color-product-second;
  box-shadow: 0px 5px 30px $bg-color-product-second;
}
.modal__milkshakes {
  background-color: $bg-color-product-third;
  box-shadow: 0px 5px 30px $bg-color-product-third;
}

.modal__button-data {
  position: absolute;
  width: 30px;
  height: 30px;
  bottom: 30px;
  left: 45%;
  border-radius: 50%;
  align-items: center;
  background-color: $main-bg-color;
  border: 1px solid rgba(0, 0, 0, 0.1);
  box-sizing: border-box;
  cursor: pointer;
  transition: fill 250ms cubic-bezier(0.4, 0, 0.2, 1), background 250ms cubic-bezier(0.4, 0, 0.2, 1);

  @media screen and (max-width: 767px) {
    bottom: 13px;
  }

  &:hover,
  &:focus {
    fill: #ffffff;
    // background: $main-hero-bg-color;
  }
}

.modal__button-close {
  width: 14px;
  height: 14px;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
}

.modal__foto {
  width: 150px;
  height: 150px;
  text-align: center;
  align-items: center;
}

