.advantages{
    padding-bottom: 97px;

    @media screen and (min-width:768px){
    }


   background-color: $second-bg-color;
}
.section__title-advantages{
    display: none;
}
.advantages__list{
    @media screen and (min-width:768px){
        display:flex;
        flex-wrap:nowrap;
        margin: -30px;  
    }

    @media screen and (min-width:1280px){
        margin: -15px;  
    }

}

.advantages__item{
    width: 100%;
    text-align: left;
    transform: rotate(2160deg) scale(0);
    opacity: 0;
    transition: all 0.4s ease 0s;
    cursor: pointer;



    &._active{
        transform: rotate(0) scale(1);
        opacity: 1;

        &:nth-child(2) {
            transition: all 0.4s ease 0.4s;
          }

          &:nth-child(3) {
            transition: all 0.4s ease 0.8s;
          }


          &:hover{
            transform: scale(1.2);
            transition: all 0.4s ease 0.2s;

            @media screen and (max-width:767px){
                transform: scale(1.1);
            }

            &:nth-child(2) {
                transition: all 0.4s ease 0s;
              }

              &:nth-child(3) {
                transition: all 0.4s ease 0s;
              }
        }

        &:hover .advantages__title{
            transition: all 0.4s ease 0.4s;
            transform: translate(30%, 0px);  

        }

    }


    @media (max-width: 767px){
        width: 230px;
    &:not(:last-child) {
        margin-bottom: 38px;
    }
}
